#!/bin/bash -ue

echo "Commencing build..."

npm run lint
if [ $? -eq 1 ];
then
    echo "There are linting errors. Failing build..."
    exit 1
fi

npm test -- --coverage
if [ $? -eq 1 ];
then
    echo "There are testing errors. Failing build..."
    exit 1
fi

npm run build
if [ $? -eq 1 ];
then
    echo "There are build errors. Failing build..."
    exit 1
fi
