import _ from 'lodash';

export default function getRouteUrl(routeString, params = {}) {
  // If no route string, return null
  if (!routeString) return null;
  let linkString = routeString;

  // Loop over params and replace
  _.each(params, (value, key) => {
    linkString = linkString.replace(`:${key}`, value);
  });

  return linkString;
}
