import _ from 'lodash';

export default function joinRequestParameters(requestParams) {
  if (!requestParams) return '';
  // Return the parameter if it's already a string.
  if (typeof requestParams === 'string') return requestParams;

  // Map the params to an array of key=val, and join them.
  const requestParameterString = _.map(requestParams, (val, key) => (`${key}=${val}`)).join('&');

  return `?${requestParameterString}`;
}
