import _ from 'lodash';

// Maps the values passed from props to pure language IDs, using the value stored
// in the valueId string passed in as a parameter.
//
// ex.
// values.myPropNameEN-US = "hello world";
// mapValuesToLanguageIds({ values, languages }, 'myPropName')
//    -> { EN-US: "hello world" }
export default function mapValuesToLanguageIds({ values, languages }, valueId) {
  return _.reduce(languages, (acc, language) => ({
    ...acc,
    [language.id]: values[`${valueId}${language.id}`],
  }), {});
}
