import { camelcaseToHyphen } from './camelCaseToHyphen';

export default function setId(prefix, uniqueId) {
  // Take in two strings and return them in the preferred format for IDs.
  // Ex. (examplePrefix, myUniqueId) => `example-prefix-my-unique-id`
  //
  // Camelcased strings will be changed to hyphens based on capitalization.
  // If hyphens should not be added to the prefix or the unique ID, they should
  // be passed in as lowercase strings.
  //
  // Extra spaces will be removed from the uniqueId.
  const uniqueIdReplace = uniqueId.toString().replace(/ /g, '');
  return camelcaseToHyphen(`${prefix}-${uniqueIdReplace}`).toLowerCase();
}
