import React from 'react';
import PropTypes from 'prop-types';
import { mergeClassNames } from '../lib/mergeClassNames';
import htmlProps from '../lib/htmlProps';

export function Icon(props) {
  const { className, name } = props;
  return (
    <span
      {...htmlProps(props)}
      className={mergeClassNames('icon iconic', className)}
      data-glyph={name}
    />
  );
}

Icon.propTypes = {
  className: PropTypes.string,
  name: PropTypes.string.isRequired,
};

Icon.defaultProps = {
  className: '',
};

export function IconText({ name, label, size }) {
  return (
    <span className="icon-text">
      <span className={`icon iconic${size ? ` ${size}` : ''}`} data-glyph={name} />
      <span className={`${size ? ` ${size}` : ''}`}>{label}</span>
    </span>
  );
}

{
  const { string, oneOf } = PropTypes;
  IconText.propTypes = {
    name: string.isRequired,
    label: string.isRequired,
    size: oneOf(['small', 'large']),
  };
}

IconText.defaultProps = {
  size: null,
};

export function HelpIcon({ className, size }) {
  return <span className={`help-icon ${size} ${className}`} />;
}

HelpIcon.propTypes = {
  size: PropTypes.oneOf(['small', 'medium', 'large']).isRequired,
  className: PropTypes.string,
};

HelpIcon.defaultProps = {
  className: '',
};

export default {
  IconText,
  Icon,
};
