import React from 'react';
import PropTypes from 'prop-types';

/** Basic Modal */
export function Modal({ isOpen, children, onBackgroundClick, size }) {
  return (
    <div className={`kubra-modal-bg ${(isOpen ? 'open' : 'closed')}`} onClick={onBackgroundClick} role="button" tabIndex="-1">
      <div className={`modal-dialog modal-${size}`}>
        <div
          className="modal-content"
          role="presentation"
          onClick={(e) => { e.stopPropagation(); }}
        >
          { children }
        </div>
      </div>
    </div>
  );
}

Modal.defaultProps = {
  // default onBackgroundClick to do nothing
  onBackgroundClick: () => null,
  size: 'md',
};

Modal.propTypes = {
  /** Modal Content */
  children: PropTypes.oneOfType([
    PropTypes.element,
  ]).isRequired,
  /** Boolean to show/hide modal */
  isOpen: PropTypes.bool.isRequired,
  /** Click handler for background click (.kubra-modal-bg) */
  onBackgroundClick: PropTypes.func.isRequired,
  /** Preset sizes (sm, md, or lg) */
  size: PropTypes.oneOf(['sm', 'md', 'lg']),
};


export default {
  Modal,
};
