import React from 'react';
import PropTypes from 'prop-types';
import { mergeClassNames } from '../lib/mergeClassNames';
import htmlProps from '../lib/htmlProps';

export function Reveal(props) {
  const { shouldReveal, children, className } = props;
  const styles = {
    display: shouldReveal ? 'inherit' : 'none',
  };
  return (
    <div {...htmlProps(props)} style={styles} className={mergeClassNames('reveal', className)}>
      {children}
    </div>
  );
}

Reveal.propTypes = {
  shouldReveal: PropTypes.bool.isRequired,
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
};

Reveal.defaultProps = {
  className: '',
};

export default Reveal;
