// Compare StringA with StringB. Return the values that are different between the two.
// Differences are not necessarily adjacent. Length of strings is taken into account.
export default (stringA, stringB) => {
  const difference = [];
  let j = 0;
  let lengthDiff = stringA.length - stringB.length;
  for (let i = 0; i < stringA.length; i += 1) {
    if (stringA.charAt(i) !== stringB.charAt(i + j)) {
      difference.push(stringA.charAt(i));
      if (lengthDiff > 0) {
        j += 1;
        lengthDiff -= 1;
      } else if (lengthDiff < 0) {
        j -= 1;
        lengthDiff += 1;
      }
    }
  }

  return difference.join('');
};
