import React, { Component } from 'react';
import autobind from 'class-autobind';
import PropTypes from 'prop-types';
import _ from 'lodash';

export class TableBody extends Component {
  constructor(props) {
    super(props);
    autobind(this);
  }
  render() {
    const { orderTable: { collection, form, updateHandler } } = this.context;
    const formTable = _.keyBy(form, 'id');
    return (
      <tbody>
        {_.sortBy(collection, 'order').map(item =>
          this.props.children(item.data, formTable[item.id].order, updateHandler(item.id)),
        )}
      </tbody>
    );
  }
}

{
  const { func, shape, arrayOf } = PropTypes;
  TableBody.propTypes = {
    children: func.isRequired,
  };
  TableBody.contextTypes = {
    orderTable: shape({
      collection: arrayOf(shape({})).isRequired,
      updateHandler: func.isRequired,
      form: arrayOf(shape({})).isRequired,
    }).isRequired,
  };
}

export default TableBody;
