import React from 'react';
import PropTypes from 'prop-types';

export function TopBar({ children }) {
  return (
    <div className="topbar">
      {children}
    </div>
  );
}

{
  const { node } = PropTypes;
  TopBar.propTypes = {
    children: node,
  };
}

TopBar.defaultProps = {
  children: <div />,
};

export function TopBarItem({ withPadding, isLast, children }) {
  const classes = `item${withPadding ? ' with-padding' : ''}${isLast ? ' last' : ''}`;
  return (
    <div className={classes}>
      {children}
    </div>
  );
}

{
  const { bool, node } = PropTypes;
  TopBarItem.propTypes = {
    withPadding: bool,
    isLast: bool,
    children: node,
  };
}

TopBarItem.defaultProps = {
  withPadding: true,
  isLast: false,
  children: <div />,
};

export default {
  TopBar,
  TopBarItem,
};
